  `workflow_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `is_default` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'is this the default message template for the workflow referenced by workflow_id?',
